INSERT INTO `hotel_comment` (`id`, `name`, `hotel`, `comment`, `comment_stars`, `comment_image`, `valid`) VALUES
(19, '1@gmail.com', '和苑三井花園飯店台北忠孝', '價錢非常吸引人，\r\n床舖也非常乾淨。\r\n自行入住還蠻簡單的，\r\n過程沒有太多困難。\r\n\r\n不喜歡 · 1010的蓮蓬頭拜託換掉，\r\n水壓也許是夠的，\r\n但蓮蓬頭漏水漏一大半，\r\n洗澡像是在澆花。\r\n浴室顯然有毛髮未清理乾淨，\r\n不用給太多餅乾茶包，\r\n就拿去換個蓮蓬頭吧！', 5, 'uploads\\1676275538615.jpg,uploads\\1676275538615.jpg,uploads\\1676275538617.jpg,uploads\\1676275538619.jpg', 1),
(20, '1@gmail.com', '和苑三井花園飯店台北忠孝', '空調好像有中央通風，有聞到別的客人吃麻油雞的味道哈哈，因為前一天很晚到，隔天8點離開，只待了八小時，這價錢無可挑剔。\r\n \r\n喜歡 · 這個價錢CP值很高了，雖然一層樓閣了很多間，但隔音意外還不錯（也有可能因為太累聽不到）。\r\n\r\n不喜歡 · 床單上有頭髮。', 5, 'uploads\\1676275570985.jpg,uploads\\1676275570986.jpg,uploads\\1676275570987.jpg', 1),
(21, '1@gmail.com', '旅樂序精品旅館站前五館', '我今天住了很開心', 4, 'uploads\\1676279561311.jpg,uploads\\1676279561312.jpg,uploads\\1676279561314.jpg,uploads\\1676279561315.jpg', 1);
