INSERT INTO `trip_event` (`id`, `trip_id`, `email`, `trip_name`, `start_date`, `end_date`, `address`, `geo_locationX`, `geo_locationY`, `area`, `region`, `introduction`, `intro_pic`, `pic_intro`, `all_pic`, `comment_grade`, `comment_amount`, `created_at`, `valid`) VALUES
(1, 't1', 'jack123@gmail.com', '台北｜北投＆陽明山一日遊', '2023-01-30 00:00:00', '2025-01-30 00:00:00', '臺北市陽明山竹子湖路1號', '121.533012', '25.042385', '北部', '台北', '{\"introduction\":\"逛膩了台北大都會的繁華世界，想到郊外踏青卻又擔心舟車勞頓？別懷疑，陽明山國家公園與北投一日遊就是你的最佳選擇。距離台北市區車程不到1小時即可抵達，我們首先將帶你走訪北投地熱谷，一睹蒸氣奔騰，磺泉玉霧的奇幻風景。接著還要去陽明山國家公園著名的花鐘地標，以及由七星山和七股山共同熔岩而成的堰塞湖──冷水坑窪地。行程最後，還將專車接送你至北台灣最熱鬧的士林夜市下車，讓你好好探索台灣的道地美食和小吃！\"}', 't1_001.webp,t1_002.webp,t1_003.webp', '悠閒漫步於陽明山國家公園，欣賞美麗的花海, 親眼見證火山活動的奔騰奇景，感受大自然的魔幻力量 , 漫步在隨風搖曳的芒草田邊，任由微風輕拂你的臉', 't1_001.webp,t1_002.webp,t1_003.webp', 450, 100, '2023-01-30 00:00:00', 1),
(2, 't2', 'jack123@gmail.com', '台北坪林 & 象山探索一日遊', '2023-01-30 00:00:00', '2025-01-30 00:00:00', '台北市大安區新生南路一段67號10652', '121.533216', '25.042455', '北部', '台北', '{\"introduction\":\"看到風和日麗的大好天氣，整顆心肯定在室內呆不住，欲踏出戶外親近自然，呼吸最純淨的空氣，探秘幽然祕境，放鬆心情，拍攝靚麗風景照，通過Klook客路預訂台北坪林& 象山探索一日遊即可滿足你！走訪近郊知名茶鄉坪林，茶園幽靜氣氛令人頓時身心鬆弛，在此領略淵博的中國傳統茶文化，飽覽大片碧綠的茶園和山巒溪谷美景；打卡人氣景點千島湖，嬌豔風光渾然天氣，可媲美桂林山水，漫步深坑老街，嘗試知名美味小吃「臭豆腐」，最後前往象山，近距離觀看台北101大樓，將城市廣闊全景盡收眼底！\" }', 't2_001.webp,t2_002.webp', '走訪坪林茶山，體驗最原生態的自然景觀,跟著當地茶農一起探討純樸的茶鄉文化,千島湖一片鬱鬱蔥蔥，張揚無限生機和活力', 't2_001.webp,t2_002.webp,t2_003.webp', 440, 100, '2023-01-30 00:00:00', 1),
(3, 't3', 'jack123@gmail.com', '台北｜九份＆十分＆野柳一日遊｜台北車站出發', '2023-01-30 00:00:00', '2025-01-30 00:00:00', '臺北市中正區黎明里北平西路3 號', '121.517058', '25.047176', '北部', '台北', '{\"introduction\":\"搭乘接駁巴士，從台北出發，輕鬆玩遍野柳、九份和十分！中途停留野柳、陰陽海、十三層遺址、黃金瀑布、九份及十分，台灣東北角看透透！同車乘客幾乎都是來自世界各地的背包客，盡情在車上聊著彼此的故事，分享在台北旅行的大小點滴、旅途趣事，甚至能結交志同道合的朋友，讓路途不再單調乏味，而是充滿驚喜歡樂！\"}', 't3_001.webp,t3_002.webp,t3_003.webp', '野柳地質公園,十分老街：天燈體驗,十分瀑布', 't3_001.webp,t3_002.webp,t3_003.webp', 430, 100, '2023-01-30 00:00:00', 1),
(4, 't4', 'jack123@gmail.com', '新北｜石碇茶鄉半日遊', '2023-01-30 00:00:00', '2025-01-30 00:00:00', '台北市大安區新生南路一段67號10652', '121.533216', '25.042455', '北部', '新北', '{\"introduction\":\"觀賞石碇千島湖的壯麗仙境，遠處的層層山巒被湖水切割，宛若無數湖島交錯，湖光山色，構成美麗的風景。適合散心的踏青路線，享受悠然閒適的體驗。 5小時的養生閒情時光，不急不躁，舒緩平日累積的辛勞。走訪坪林茶業博物館，享受陣陣茶香。走入八卦茶園，遠眺附近的茶園風光及北勢溪風景。附近坡地種滿茶樹，形成層疊的梯田景緻，壯觀又優美。這裡提供茶飲及餐點，可以在此坐看茶園風光，欣賞美景同時，品茗台灣名產烏龍茶，絕對是台灣旅遊的絕佳享受！\",', 't4_001.webp,t4_002.webp', '參觀翠綠的八卦茶園，在此賞美景、品好茶,山與水的蜿蜒交錯，增添千島湖的仙境色彩,造訪坪林茶業博物館，認識台灣茶知識', 't4_001.webp,t4_002.webp,t4_003.webp', 420, 100, '2023-01-30 00:00:00', 1),
(5, 't5', 'jack123@gmail.com', '台北 | 經典行程一日遊', '2023-01-30 00:00:00', '2025-01-30 00:00:00', '台北市萬華區成都路10號', '121.506905', '25.042050', '北部', '台北', '{\"introduction\":\"展開台北市區一日遊，盡覽故宮、台北101、大稻埕等不同景點樣貌，走訪懷舊氣息濃厚的四四南村，到故宮了解台灣文物歷史，欣賞台北獨特的人文地景景象，用相機鏡頭捕捉美好回憶\"}', 't5_001.webp,t5_002.webp,t5_003.webp', '西門町,龍山寺,國立故宮博物院', 't5_001.webp,t5_002.webp,t5_003.webp', 410, 100, '2023-01-30 00:00:00', 1),
(6, 't6', 'jack123@gmail.com', '新北｜平溪十分天燈半日遊', '2023-01-30 00:00:00', '2025-01-30 00:00:00', '新北市平溪區靜安路2段141號', '121.722485', '25.023316', '北部', '台北', '{\"introduction\":\"前往迷人的鐵道小鎮平溪，度過「十分」悠閒的一日遊。平溪白天也可以燃放天燈，感受電影《那些年，我們一起追的女孩》裡的青春情懷。在五顏六色的天燈上寫下自己的願望，讓你的祈禱乘風飛揚，直抵雲端。平溪線是為觀光客特別是鐵路迷而設，小火車外部鐵皮繪有美麗的圖畫，只有3節車廂左右，沿著蜿蜒的鐵道叮叮噹噹地穿過山間小鎮，經歷了煤礦興衰的平溪老街，彷彿凝固了時光，還保留著滄桑古樸的牆壁和招牌，濃濃的古早風情和懷舊氣息瀰漫街頭。之後還可以前往周邊的十分瀑布，氣勢恢宏的十分瀑布聲勢浩大，非常壯觀。\"}', 't6_001.webp,t6_002.webp,t6_003.webp', '平溪是台灣著名的放天燈許願之地\",\"蜿蜒的鐵道，濃濃的古早風情，懷舊氣息滿滿的街景非常適合拍照\",\"十分瀑布是台灣最大的垂簾型瀑布，有台灣尼加拉瓜瀑布之美稱', 't6_001.webp,t6_002.webp,t6_003.webp', 400, 100, '2023-01-30 00:00:00', 1),
(7, 't7', 'jack123@gmail.com', '台北｜夜迷之行：鼎泰豐晚餐＆饒河夜市', '2023-01-30 00:00:00', '2025-01-30 00:00:00', '臺北市萬華區富福里康定路382 號', '121.500326', '25.033383', '北部', '台北', '{\"introduction\":\"俗話有云：入屋叫人，進廟拜神。來到台北鎮廟銅鑄龍柱、百尊眾神雕像、鐘樓、古樓、魚貫的善男信女等，讓這裡瀰漫著濃厚的文化風韻。行程還會帶你來到饒河夜市，體驗道地台式風味。最後前往台北101，享用鼎泰豐晚餐，走走逛逛，以美食為夜晚劃下完美句點。\"}', 't7_001.webp,t7_002.webp,t7_003.webp', '夜遊台北熱門景點，見證台北別樣風貌,台北101鼎泰豐享用晚餐，以美食劃下夜晚完美句點,饒河夜市美食走透透', 't7_001.webp,t7_002.webp,t7_003.webp', 390, 100, '2023-01-30 00:00:00', 1),
(8, 't8', 'jack123@gmail.com', '台北｜賞花一日遊', '2023-01-30 00:00:00', '2025-01-30 00:00:00', '臺北市陽明山竹子湖路1號', '121.538431', '24.952085', '北部', '台北', '{\"introduction\":\"春季限定賞櫻之旅！讓我們帶您一日走訪台北最熱門的賞櫻景點，讓滿滿的櫻花為您的旅途增添獨一無二的色彩。行程結合外語導覽(中文和英文)，包含都市後花園「士林官邸」和「陽明山國家公園」、北台灣最知名的賞櫻聖地「淡水天元宮」，以及「淡水漁人碼頭」享受浪漫夕照。\"}', 't8_001.webp,t8_002.webp,.t8_003.webp', '士林官邸,陽明山國家公園,淡水天元宮', 't8_001.webp,t8_002.webp,.t8_003.webp', 380, 100, '2023-01-30 00:00:00', 1),
(9, 't9', 'jack123@gmail.com', '台北｜碧潭風景區SUP立槳體驗', '2023-01-30 00:00:00', '2025-01-30 00:00:00', '新北市新店區新店路67號', '121.538431', '24.952085', '北部 ', '台北', '{\"introduction\":\"新店區碧潭風景區是在台北度過愉快週末的好去處，經常會有人來這裡釣魚、燒烤等。而在這個地區最受歡迎的活動之一，莫過於在新店河體驗SUP立式划槳！該地區擁有豐富的池塘、階地和沙洲，還有很多值得一看的地方。你會驚嘆於水流的天然海藍色，宛如一顆寶石呈現在眼前。你將跟隨你的中／英文教練，學習划水的同時欣賞著周圍美麗的景色。一定要仔細聽講哦！這樣你才可以學到更多關於立式划槳的知識和技巧。此活動還包含拍照服務，請在鏡頭前盡情地展示你的笑容吧，讓照片銘記當時的歡樂！\"}', 't9_001.webp,t9_002.webp,t9_003.webp', '無論你是初學者還是經驗老道的划槳達人，這項活動都保證讓你興奮無比！,在中／英文教練的指導下學習，逐步掌握這項水上活動,在熱門的碧潭風景區周圍，開啟刺激無憂的旅程', 't9_001.webp,t9_002.webp,t9_003.webp,t9_004.webp,t9_005.webp,t9_006.webp,t9_007.webp', 450, 100, '2023-01-31 00:00:00', 1),
(10, 't10', 'jack123@gmail.com', '桃園 Xpark 水族館門票', '2023-01-30 00:00:00', '2025-01-30 00:00:00', '台灣桃園市中壢區春德路105號', '121.214153', '25.017382', '北部 ', '桃園', '{\"introduction\":\"Xpark 為台灣首座新都會型水生公園。將生活在地球上各種地域的生物們的環境，透過空間演出與科技的融合，加以忠實的重現。在連氣溫、濕度、味道及聲音都經過縝密計算的空間裡，從天花板到地坪、延伸至水槽的影像演出呈現出 360° 具魄力的沉浸式空間。來訪旅客彷彿真的身歷其境，使用五感體驗各真實場景。主角，是生活在那裡的生物們，隨環境變遷而演變進化的生物們的不思議，從各種角度將其魅力性襯托出來的環境演出也是一大特徵。Xpark 是一個滿足人們無止盡的「對於求知的慾望和獲知的喜悅」，並在世界上也是獨一無二的寓教於樂設施。\"}', 't10_001.webp,t10_002.webp,t10_003.webp', 'Xpark展區：福爾摩沙,珊瑚潛行,癒見水母', 't10_001.webp,t10_002.webp,t10_003.webp', 430, 100, '2023-01-30 00:00:00', 1),
(11, 't11', 'jack123@gmail.com', '基隆國立海洋科技博物館門票：主題館・潮境智能館・特展', '2023-01-30 00:00:00', '2025-01-30 00:00:00', '基隆市中正區北寧路 367號', '121.798852', '25.140198', '北部 ', '基隆', '{\"introduction\":國立海洋科技博物館的主題館共有八大展廳與一個特展廳，八大展廳為常設展廳包含：海洋環境廳、海洋科學廳、船舶與海洋工程廳、水產廳、海洋文化廳、深海影像廳、深海展示廳、兒童廳，每個展廳都各具特色並有豐富多元的展項，特展廳則是會推出不同主題的展覽，每個展覽都將帶給大家不同的驚喜。透過走訪每個展廳，你將會深刻的瞭解到，在我們的日常裡原來海洋與我們是多麼的息息相關，大到環境與氣候，小到日常用品，無不與海洋有著深刻的連結，如果想要「認識海洋、親近海洋、永續海洋」，主題館絕對是必訪的一個地方。\"}', 't11_001.webp', '探索海博館，深入認識海洋科學和技術知識,館內設有海洋文化廳、海洋科學廳、水產廳與海洋環境廳等多個主題展覽,參觀各類主題展覽，充分認識海洋生態的重要性與獨特性', 't11_001.webp,t11_002.webp,t11_003.webp', 420, 100, '2023-01-30 00:00:00', 1),
